window.onload=function(){for(var e=document.querySelectorAll(".buy-btn"),n=document.querySelector(".confirm-block"),c=document.querySelector(".continue"),o=document.querySelector(".confirm-close-btn"),t=0;t<e.length;t++)e[t].addEventListener("click",function(e){e.preventDefault(),n.classList.add("confirm-block-show")});c.addEventListener("click",function(e){e.preventDefault(),n.classList.remove("confirm-block-show")}),o.addEventListener("click",function(e){e.preventDefault(),n.classList.remove("confirm-block-show")})};
