window.onload=function(){var e=document.querySelector("#mini-map"),t=document.querySelector(".modal-map"),o=document.querySelector(".modal-map-close"),c=document.querySelector("#contact-form"),n=document.querySelector(".wright-us"),r=document.querySelector(".modal-form-close");e.addEventListener("click",function(e){e.preventDefault(),t.classList.add("modal-map-show")}),o.addEventListener("click",function(e){e.preventDefault(),t.classList.remove("modal-map-show")}),c.addEventListener("click",function(e){e.preventDefault(),n.classList.add("wright-us-show")}),r.addEventListener("click",function(e){e.preventDefault(),n.classList.remove("wright-us-show")})};
